import { addDoc, getDocs, collection, query, where } from "firebase/firestore";
import { db, auth } from "../firebase-config";
import { useAuthState } from "react-firebase-hooks/auth";
import { useEffect, useState } from "react";

export const Review = (props) => {
    const { review } = props;
    const [user] = useAuthState(auth);

    const [likeCount, setLikeCount] = useState(null);

    const likesRef = collection(db, "likes");

    const likesDoc = query(likesRef, where("reviewId", "==", review.id));

    const addLike = async () => {
        await addDoc(likesRef, { userId: user?.uid, reviewId: review.id }); 
    };

    const getLikes = async () => {
        const data = await getDocs(likesDoc)
        setLikeCount(data.docs.length);
    };

    useEffect(() => {
        getLikes();
    }, []);

    return (
        <div className="reviewContainer">
            <div className="title">
                <h3>{review.title}</h3>
            </div>
            <div className="body">
                <p>{review.description}</p>
            </div>

            <div className="footer">
                <p>From: {review.username}</p>
                {likeCount && <p className="likes"> Likes: {likeCount} </p>}
                <button onClick={addLike}><img src={'/images/thumbsup.png'} height="20px"/></button>
                <br></br><br></br>
            </div>
        </div>
    );
};
